arguments.argument:
x = mouse_x;
y = mouse_y;

if (collision_point(x, y, Button, true, true)>0) || (collision_point(x, y, ToolBarTop, true, true)>0)
{
    mc.currentTool = "";
    global.notOnTools = false;
}
else if (collision_point(x, y, Button, true, true)<0)
{
    mc.currentTool = global.tool;
    global.notOnTools = true;
}
