arguments.argument:
{
  // ensure we don't scroll past the edges of the level
  if(view_yview[0] > 6 * background_get_height(levelbg) - view_hview[0]) view_yview[0] = 6 * background_get_height(levelbg) - view_hview[0];
  if(view_xview[0] > 6 * background_get_width(levelbg) - view_wview[0]) view_xview[0] = 6 * background_get_width(levelbg) - view_wview[0];
  if(view_yview[0] < 0) view_yview[0] = 0;
  if(view_xview[0] < 0) view_xview[0] = 0;
}
